# Cell
import re
import nltk
from nltk.corpus import stopwords

# Cell
nltk.download('stopwords', quiet=True)
stopword = set(stopwords.words('english'))

# Cell
def text_clean(txt):
  txt = re.sub('[^A-Za-z0-9]+', ' ', txt)
  txt = txt.lower()
  pattern = re.compile(r'\b(' + r'|'.join(stopword) + r')\b\s*')
  txt = pattern.sub(' ', txt)
  txt = ' '.join(txt.split())
  return txt